class Contact{constructor(t,e,o){this.person=t,this.phone=e,this.cellPhone=o}}class Person{constructor(t,e){this.name=t,this.birthDate=new Date(e)}formatDate(){return`${this.birthDate.getDay()}/${this.birthDate.getDate()}/${this.birthDate.getFullYear()}`}}class Phone{constructor(t,e,o){this.DDD=t,this.number=e,this.type=o}formatPhone(){return`(${this.DDD}) ${this.number}`}}class ContactController{constructor(){this.xhr=new XMLHttpRequest}getContact(t){this.xhr.open("GET","/contact",!0),this.xhr.onload=(()=>(200===this.xhr.status?console.log(this.xhr.response):console.error("error"),t(JSON.parse(this.xhr.response)))),this.xhr.send()}}class ContactView{constructor(){this.contacts=ko.observableArray([]),this.contactController=new ContactController}setObservable(){this.contactController.getContact(t=>{t.forEach(t=>{let e=new Person(t.person.name,t.person.birthDate),o=new Phone(t.phone.DDD,t.phone.number,t.phone.type),s=new Phone(t.cellPhone.DDD,t.cellPhone.number,t.cellPhone.type);this.contacts.push(new Contact(e,o,s))}),ko.applyBindings({contacts:this.contacts})})}}